import Vue from 'vue';
import Router from 'vue-router';
Vue.use(Router);
/* Layout */

import Layout from '@/layout';
/**
 * Note: sub-menu only appear when route children.length >= 1
 * Detail see: https://panjiachen.github.io/vue-element-admin-site/guide/essentials/router-and-nav.html
 *
 * hidden: true                   if set true, item will not show in the sidebar(default is false)
 * alwaysShow: true               if set true, will always show the root menu
 *                                if not set alwaysShow, when item has more than one children route,
 *                                it will becomes nested mode, otherwise not show the root menu
 * redirect: noRedirect           if set noRedirect will no redirect in the breadcrumb
 * name:'router-name'             the name is used by <keep-alive> (must set!!!)
 * meta : {
    roles: ['admin','editor']    control the page roles (you can set multiple roles)
    title: 'title'               the name show in sidebar and breadcrumb (recommend set)
    icon: 'svg-name'/'el-icon-x' the icon show in the sidebar
    breadcrumb: false            if set false, the item will hidden in breadcrumb(default is true)
    activeMenu: '/example/list'  if set path, the sidebar will highlight the path you set
  }
 */

/**
 * constantRoutes
 * a base page that does not have permission requirements
 * all roles can be accessed
 */

export var constantRoutes = [{
  path: '/login',
  component: function component() {
    return import('@/views/login/index');
  },
  hidden: true
}, {
  path: '/404',
  component: function component() {
    return import('@/views/404');
  },
  hidden: true
}, {
  path: '/',
  component: Layout,
  redirect: '/dashboard',
  children: [{
    path: 'dashboard',
    name: 'Dashboard',
    component: function component() {
      return import('@/views/dashboard/index');
    },
    meta: {
      title: '淘淘客栈',
      icon: 'dashboard'
    }
  }]
}, {
  path: '/task',
  component: Layout,
  redirect: '/task/list',
  name: '任务管理',
  meta: {
    title: '任务管理',
    icon: 'el-icon-s-help'
  },
  children: [{
    path: 'list',
    name: 'tasklist',
    component: function component() {
      return import('@/views/task/list');
    },
    meta: {
      title: '任务列表',
      icon: 'table'
    }
  }, {
    path: 'create-task',
    name: 'create-task',
    component: function component() {
      return import('@/views/form/task/createTask');
    },
    meta: {
      title: '创建任务',
      icon: 'form'
    }
  }, {
    path: 'edit-task',
    name: 'edit-task',
    component: function component() {
      return import('@/views/form/task/editTask');
    },
    hidden: true
  }]
}, {
  path: '/approve',
  component: Layout,
  redirect: '/approve/list',
  name: '审核管理',
  meta: {
    title: '审核管理',
    icon: 'el-icon-s-help'
  },
  children: [{
    path: 'list',
    name: 'approveList',
    component: function component() {
      return import('@/views/approve/list');
    },
    meta: {
      title: '审核列表',
      icon: 'table'
    }
  }, {
    path: 'approve-process',
    name: 'approve-process',
    component: function component() {
      return import('@/views/approve/process');
    }
  }]
}, // 404 page must be placed at the end !!!
{
  path: '*',
  redirect: '/404',
  hidden: true
}];

var createRouter = function createRouter() {
  return new Router({
    // mode: 'history', // require service support
    scrollBehavior: function scrollBehavior() {
      return {
        y: 0
      };
    },
    routes: constantRoutes
  });
};

var router = createRouter(); // Detail see: https://github.com/vuejs/vue-router/issues/1234#issuecomment-357941465

export function resetRouter() {
  var newRouter = createRouter();
  router.matcher = newRouter.matcher; // reset router
}
export default router;
