import { fetchList } from "@/api/task";

export default {
  filters: {
    statusFilter(status) {
      const statusMap = {
        published: "success",
        draft: "gray",
        deleted: "danger"
      };
      return statusMap[status];
    }
  },
  data() {
    return {
      list: null,
      listLoading: true,
      listQuery: {
        page: 1,
        limit: 20,
        name: undefined
      }
    };
  },
  created() {
    this.fetchData();
  },
  methods: {
    handleFilter() {
      fetchList(this.listQuery).then(res => {
        this.list = res;
        this.listLoading = false;
      });
    },
    fetchData() {
      this.listLoading = true;
      fetchList().then(response => {
        this.list = response;
        this.listLoading = false;
      });
    },
    jumpToDetail(taskId) {
      this.$router.push({
        name: "edit-task",
        params: { taskId: taskId }
      });
    }
  }
};
