import { fetchList, audit } from "@/api/approve";
import { MessageBox, Message } from 'element-ui'
export default {
  filters: {
    statusFilter(status) {
      const statusMap = {
        published: "success",
        draft: "gray",
        deleted: "danger"
      };
      return statusMap[status];
    }
  },
  data() {
    return {
      list: null,
      listLoading: true,
      listQuery: {
        status: undefined,
        page: 1,
        limit: 20,
        taskName: undefined
      },
      options: [
        {
          value: 20,
          label: "身份审核"
        },
        {
          value: 40,
          label: "任务审核"
        }
      ]
    };
  },
  created() {
    this.fetchData(20);
  },
  methods: {
    handleFilter() {
      if (!this.listQuery.status) {
        Message({
          message: "请选择审核状态",
          type: "error",
          duration: 5 * 1000
        });
        return;
      }
      this.fetchData(this.listQuery.status);
    },
    fetchData(status) {
      this.listLoading = true;
      fetchList({ status: status }).then(response => {
        this.list = response;
        this.listLoading = false;
      });
    },
    jumpToDetail(id) {
      this.$router.push({
        name: "approve-process",
        params: { processId: id }
      });
    }
  }
};
