// import Tinymce from '@/components/Tinymce'
import Upload from "@/components/Upload/singleImage";
import MDinput from "@/components/MDinput";
// import Multiselect from 'vue-multiselect'// 使用的一个多选框组件，element-ui的select不能满足所有需求
// import 'vue-multiselect/dist/vue-multiselect.min.css'// 多选框组件css
import Sticky from "@/components/Sticky"; // 粘性header组件
// import { validateURL } from '@/utils/validate'
import { fetchTask, createTask } from "@/api/task";
// import { userSearch } from '@/api/remoteSearch'

const defaultTask = {
  status: "draft",
  name: "", // 任务名
  description: "", // 任务描述
  reward: "", // 任务奖励
  searchKeyword: "", //搜索关键字
  pictureUrl: "", // 文章图片
  displayTime: undefined, // 前台展示时间
  id: undefined,
  platforms: ["a-platform"],
  comment_disabled: false
};

export default {
  name: "taskDetail",
  components: { Sticky, MDinput, Upload },
  props: {
    isEdit: {
      type: Boolean,
      default: false
    }
  },
  data() {
    const validateRequire = (rule, value, callback) => {
      if (value === "") {
        this.$message({
          message: rule.field + "为必传项",
          type: "error"
        });
        callback(null);
      } else {
        callback();
      }
    };
    const validateSourceUri = (rule, value, callback) => {
      if (value) {
        if (validateURL(value)) {
          callback();
        } else {
          this.$message({
            message: "外链url填写不正确",
            type: "error"
          });
          callback(null);
        }
      } else {
        callback();
      }
    };
    return {
      src:"https://cube.elemecdn.com/6/94/4d3ea53c084bad6931a56d5158a48jpeg.jpeg",
      postTask: Object.assign({}, defaultTask),
      fetchSuccess: true,
      loading: false,
      userLIstOptions: [],
      platformsOptions: [
        { key: "a-platform", name: "a-platform" },
        { key: "b-platform", name: "b-platform" },
        { key: "c-platform", name: "c-platform" }
      ],
      rules: {
        pictureUrl: [{ validator: validateRequire }],
        name: [{ validator: validateRequire }],
        description: [{ validator: validateRequire }],
        pictureUrl: [{ validator: validateSourceUri, trigger: "blur" }]
      }
    };
  },
  computed: {
    contentShortLength() {
      return this.postTask.description.length;
    }
  },
  created() {
    if (this.isEdit) {
      this.fetchData();
    } else {
      this.postTask = Object.assign({}, defaultTask);
    }
  },
  methods: {
    fetchData() {
      let id = this.$route.params.taskId;
      fetchTask(id)
        .then(response => {
          this.postTask = response;
        })
        .catch(err => {
          this.fetchSuccess = false;
          console.log(err);
        });
    },
    submitForm() {
      this.postTask.displayTime = parseInt(this.displayTime / 1000);
      console.log(this.postTask);
      createTask(this.postTask);
      this.$refs.postTask.validate(valid => {
        if (valid) {
          this.loading = true;
          this.$notify({
            title: "成功",
            message: "发布文章成功",
            type: "success",
            duration: 2000
          });
          this.postTask.status = "published";
          this.loading = false;
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    },
    draftForm() {
      if (
        this.postTask.description.length === 0 ||
        this.postTask.name.length === 0
      ) {
        this.$message({
          message: "请填写必要的标题和内容",
          type: "warning"
        });
        return;
      }
      this.$message({
        message: "保存成功",
        type: "success",
        showClose: true,
        duration: 1000
      });
      this.postTask.status = "draft";
    }
    // getRemoteUserList(query) {
    //   userSearch(query).then(response => {
    //     if (!response.data.items) return;
    //     console.log(response);
    //     this.userLIstOptions = response.data.items.map(v => ({
    //       key: v.name
    //     }));
    //   });
    // }
  }
};
